// SPDX-License-Identifier: MITX
pragma solidity 0.8.7;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Market is ReentrancyGuard, Pausable, Ownable {
    // ERRORS
    // "Market: Market doesn't has approved status for this item!"
    error ItemNotApproved();
    
    using Counters for Counters.Counter;
    Counters.Counter private _auctionCount;
    Counters.Counter private _offersCount;
    Counters.Counter private _salesCount;

    mapping(uint256 => Auction) public auctions;
    mapping(uint256 => Offer) public offers;
    mapping(uint256 => Sale) public sales;

    // Event enums
    enum TypeAuction {create, update, cancel, settle}
    enum TypeOffer {create, accept, decline}
    enum TypeSale {create, accept, update, cancel}

    struct Auction {
        uint256 id;
        uint256 itemId;
        uint256 reservePrice;
        uint256 expireAt;
        address previousBidder;
        address currentBidder;
        address seller;
        address contractAddress;
        bool sold;
    }

    struct Offer {
        uint id;
        uint256 itemId;
        uint256 amount;
        uint256 expireAt;
        address contractAddress;
        address offerFrom;
        bool sold;
    }

    struct Sale {
        uint id;
        uint itemId;
        uint256 amount;
        address contractAddress;
        address seller;
        bool sold;
    }

    event EventAuction (
        uint256 id,
        uint256 indexed itemId,
        uint256 reservePrice,
        uint256 expireAt,
        address previousBidder,
        address currentBidder,
        address seller,
        address indexed contractAddress,
        bool sold,
        TypeAuction indexed eventType
    );

    event EventOffer (
        uint id,
        uint256 indexed itemId,
        uint256 amount,
        uint256 expireAt,
        address indexed contractAddress,
        address offerFrom,
        bool sold,
        TypeOffer indexed eventType
    );

    event EventSale (
        uint id,
        uint indexed itemId,
        uint256 amount,
        address indexed contractAddress,
        address seller,
        bool sold,
        TypeSale indexed eventType
    );

    modifier _auctionExists(uint256 _auctionId) {
        // Check if auction exist's
        require(_auctionId <= _auctionCount.current(), "Market: Invalid auction id!");
        _;
    }

    modifier _offerExists(uint256 _offerId) {
        // Check if offer exist's
        require(_offerId <= _offersCount.current(), "Market: Invalid Offer id!");
        _;
    }

    modifier _saleExists(uint256 _saleId) {
        require(_saleId <= _salesCount.current(), "Market: Invalid sale id!");
        _;
    }

    modifier _saleEnded(uint256 _saleId) {
        Sale memory sale = sales[_saleId];
        require(!sale.sold, "Market: Sale has been ended or canceled!");
        _;
    }

    // Check if this contract has approval for the item/token
    modifier _isApproved(address _contractAddress, uint256 _itemId) {
        if(IERC721(_contractAddress).getApproved(_itemId) != address(this) && IERC721(_contractAddress).ownerOf(_itemId) != address(this)){
            revert ItemNotApproved();
        }
        _;
    }

    constructor() Pausable() Ownable() {}

    function pause() external onlyOwner {
        _pause();
    }

    function unPause() external onlyOwner {
        _unpause();
    }
    
    // Create Auction
    function createReserveAuction(address _contractAddress, uint256 _itemId, uint256 _reservePrice) external returns(uint256) {
        _auctionCount.increment();
        uint256 _auctionId = _auctionCount.current();
        IERC721(_contractAddress).transferFrom(msg.sender, address(this), _itemId);
        uint256 expireAt = block.timestamp + 1 days;
        
        auctions[_auctionId] = Auction({
            id: _auctionId,
            contractAddress: _contractAddress,
            itemId:_itemId,
            reservePrice:_reservePrice,
            previousBidder:address(0),
            currentBidder: address(0),
            seller: payable(msg.sender),
            sold: false,
            expireAt: expireAt
        });

        emit EventAuction (
            _auctionId,
            _itemId,
            _reservePrice,
            expireAt,
            address(0),
            address(0),
            msg.sender,
            _contractAddress,
            false,
            TypeAuction.create
        );
        return _auctionId;
    }

    // Bid Auction
    function placeBid(uint256 _auctionId) external payable _auctionExists(_auctionId) nonReentrant whenNotPaused returns (uint256) {
        Auction storage auction = auctions[_auctionId];
        // Check if it is not expired or sold
        require(block.timestamp <= auction.expireAt, "Market: Auction has expired!");
        require(!auction.sold,"Market: Auction is no more active, Item has been sold already or removed!");
        // Send the requried amount to this contract
        require(msg.value > auction.reservePrice, "Market: Please send amount greater than reserve amount!");

        // Change state, new owner and previous owner
        address _currentBidder = auction.currentBidder;
        auction.currentBidder = payable(msg.sender);
        auction.previousBidder = payable(_currentBidder);
        // Update the reserve price to the new bid amount
        uint _previousReservePrice = auction.reservePrice;
        auction.reservePrice = msg.value;
        
        // Payback the old bidder
        if(auction.previousBidder != address(0)) {
            (bool sent, ) = auction.previousBidder.call{value: _previousReservePrice}("");
            require(sent,"Market: Bid amount has not been sent to the previous bidder.");
        }

        return auction.id;
    }

    // Settle Auction
    function settleAuction(uint256 _auctionId) external _auctionExists(_auctionId) nonReentrant returns (uint256) {
        Auction storage auction = auctions[_auctionId];

        // Only seller or currentBidder can settle this auction.
        require(msg.sender == auction.seller || msg.sender == auction.currentBidder, "Market: Only seller or current highest bidder can settle this auction!");

        // Check if it is expired or sold
        require(block.timestamp > auction.expireAt, "Market: Auction has not expired yet!");
        require(!auction.sold, "Market: Auction has been settled already!");

        // Change state, sold.
        auction.sold = true;
        // Sent the asset to the highest bidder
        IERC721(auction.contractAddress).transferFrom(address(this), auction.currentBidder, auction.itemId);
        // Transfer the bid amount to the seller
        (bool sent,) = auction.seller.call{value: auction.reservePrice}("");
        require(sent,"Market: Bid amount has not been sent to the seller.");

        emit EventAuction (
            _auctionId,
            auction.itemId,
            auction.reservePrice,
            auction.expireAt,
            auction.previousBidder,
            auction.currentBidder,
            auction.seller,
            auction.contractAddress,
            true,
            TypeAuction.settle
        );
        
        return _auctionId;
    }

    // Update Reserve Price
    function updateReservePrice(uint256 _auctionId, uint256 _reservePrice) external _auctionExists(_auctionId)  {
        Auction storage auction = auctions[_auctionId];

        // Only seller can update the auction
        require(msg.sender == auction.seller, "Market: Only the auction seller can modify the auction!");

        // Only update if the auction has not started
        require(auction.currentBidder == address(0) && auction.previousBidder == address(0), "Market: Auction has started already, cant update now!");

        // Change state
        auction.reservePrice = _reservePrice;

        emit EventAuction (
            _auctionId,
            auction.itemId,
            _reservePrice,
            auction.expireAt,
            auction.previousBidder,
            auction.currentBidder,
            auction.seller,
            auction.contractAddress,
            false,
            TypeAuction.update
        );
    }

    // Cancel the Auction
    function cancelReserveAuction(uint256 _auctionId) external _auctionExists(_auctionId) {
        Auction storage auction = auctions[_auctionId];

        // Only seller can update the auction
        require(msg.sender == auction.seller, "Market: Only the auction seller can cancel the auction!");

        // Only update if the auction has not started
        require(auction.currentBidder == address(0) && auction.previousBidder == address(0), "Market: Auction has started already, cant update now!");

        // Send back the asset to the seller
        IERC721(auction.contractAddress).transferFrom(address(this), auction.seller, auction.itemId);
        // Change state
        auction.sold = true;

        emit EventAuction (
            _auctionId,
            auction.itemId,
            auction.reservePrice,
            auction.expireAt,
            auction.previousBidder,
            auction.currentBidder,
            auction.seller,
            auction.contractAddress,
            true,
            TypeAuction.settle
        );
    }

    // OFFERS //
    // Create Offer
    function makeOffer(address _contractAddress, uint256 _itemId, uint256 _amount) external payable whenNotPaused returns(uint256) {

        require(msg.value >= _amount, "Market: Amount sent should be greater than offer amount!");

        _offersCount.increment();
        uint256 offerId = _offersCount.current();
        uint256 expireAt =  block.timestamp + 1 days;

        offers[offerId] = Offer({
            id: offerId,
            itemId: _itemId,
            amount:_amount,
            expireAt: expireAt,
            contractAddress: _contractAddress,
            offerFrom: payable(msg.sender),
            sold: false
        });

        emit EventOffer (
            offerId,
            _itemId,
            _amount,
            expireAt,
            _contractAddress,
            msg.sender,
            false,
            TypeOffer.create
        );

        return offerId;
    }

    function acceptOffer(uint256 _offerId) external nonReentrant whenNotPaused _offerExists(_offerId) {

        Offer storage offer = offers[_offerId];

        // Check if it is expired or sold
        require(block.timestamp <= offer.expireAt, "Market: Offer has expired!");
        require(!offer.sold, "Market: Offer has been settled already!");

        if(IERC721(offer.contractAddress).getApproved(offer.itemId) != address(this)){
            revert ItemNotApproved();
        }

        // Change State
        offer.sold = true;

        IERC721(offer.contractAddress).transferFrom(msg.sender, offer.offerFrom, offer.itemId);
        (bool sent,) = msg.sender.call{value: offer.amount}("");
        require(sent, "Market: Offer amount has not been sent to the asset owner.");

        emit EventOffer (
            _offerId,
            offer.itemId,
            offer.amount,
            offer.expireAt,
            offer.contractAddress,
            offer.offerFrom,
            true,
            TypeOffer.accept
        );
    }

    function cancelOffer(uint _offerId) external nonReentrant _offerExists(_offerId) {
        Offer storage offer = offers[_offerId];

        // Can be canceled by the offerer
        require(msg.sender == offer.offerFrom, "Market: Can be canceled by the offerer!");
        
        require(!offer.sold, "Market: Offer has been settled or has been canceled already!");

        offer.sold = true;
        (bool sent, ) = offer.offerFrom.call{ value: offer.amount }("");
        require(sent, "Market: Offer amount not returned to the offerer!");

        emit EventOffer (
            _offerId,
            offer.itemId,
            offer.amount,
            offer.expireAt,
            offer.contractAddress,
            offer.offerFrom,
            true,
            TypeOffer.decline
        );
    }

    // BUY //
    function setBuyPrice(address _contractAddress, uint256 _itemId, uint256 _amount) external _isApproved(_contractAddress, _itemId) returns(uint256) {
        _salesCount.increment();

        uint256 saleId = _salesCount.current();

        sales[saleId] = Sale({
            id: saleId,
            itemId: _itemId,
            amount:_amount,
            contractAddress: _contractAddress,
            seller: payable(msg.sender),
            sold: false
        });

        IERC721(_contractAddress).transferFrom(msg.sender, address(this), _itemId);

        emit EventSale (
            saleId,
            _itemId,
            _amount,
            _contractAddress,
            msg.sender,
            false,
            TypeSale.create
        );

        return saleId;
    }

    function buy(address _contractAddress, uint256 _itemId, uint256 _saleId) external payable whenNotPaused nonReentrant _isApproved(_contractAddress, _itemId) _saleExists(_saleId) _saleEnded(_saleId) {
        Sale storage sale = sales[_saleId];

        require(msg.value >= sale.amount, "Market: Send the asking price!");

        // Change state
        sale.sold = true;

        IERC721(_contractAddress).transferFrom(address(this), msg.sender, sale.itemId);
        (bool sent, ) = sale.seller.call{value:sale.amount}("");
        require(sent, "Market: Sale amount not returned to the seller!");

        emit EventSale (
            _saleId,
            _itemId,
            sale.amount,
            _contractAddress,
            sale.seller,
            true,
            TypeSale.accept
        );
    }

    function updateBuyPrice(uint256 _saleId, uint256 _amount) external _saleExists(_saleId)  _saleEnded(_saleId) {
        Sale storage sale = sales[_saleId];

        require(msg.sender == sale.seller,"Market: Only seller can update buy price!");

        // Change State
        sale.amount = _amount;

        emit EventSale (
            _saleId,
            sale.itemId,
            sale.amount,
            sale.contractAddress,
            sale.seller,
            false,
            TypeSale.update
        );
    }

    function cancelBuyPrice(uint256 _saleId) external _saleExists(_saleId) _saleEnded(_saleId) {
        Sale storage sale = sales[_saleId];

        require(msg.sender == sale.seller,"Market: Only seller can cancel the sale!");

        // Change State
        sale.sold = true;

        IERC721(sale.contractAddress).transferFrom(address(this), msg.sender, sale.itemId);

        emit EventSale (
            _saleId,
            sale.itemId,
            sale.amount,
            sale.contractAddress,
            sale.seller,
            true,
            TypeSale.cancel
        );
    }
}
